mapper.select:
SELECT * FROM user_info
WHERE ui_id = #{uiId}
AND ui_password = #{uiPassword}

mapper.update:
UPDATE user_info
SET ui_password = #{uiPassword}, ui_nickname = #{uiNickname}, 
	ui_file_path = #{uiFilePath}, ui_email = #{uiEmail}
WHERE ui_id = #{uiId}

UPDATE user_info
SET ui_nickname = #{uiNickname}
WHERE ui_id = #{uiId}

UPDATE user_info
SET ui_password = #{uiPassword}
WHERE ui_id = #{uiId}

UPDATE user_info
SET ui_email = #{uiEmail}
WHERE ui_id = #{uiId}

mapper.select:
SELECT ui.*, ri.*
FROM user_info ui, review_info ri
WHERE ui.ui_id = ri.ui_id AND ui.ui_id = #{uiId}
